    private void btnLagreActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnLagreActionPerformed
        // TODO add your handling code here:
        // ingen feilh√•ndtering dersom fornavn og/eller etternavn ikke er fylt ut
        String fnavn = txtFornavn.getText().toUpperCase();
        String enavn = txtEtternavn.getText().toUpperCase();
        p = new Person(-1, fnavn, enavn);
        setVisible(false);
        dispose();
    }//GEN-LAST:event_btnLagreActionPerformed
    
